mapper.update:
update SYS_APP_USER set 
LAST_LOGIN = #{LAST_LOGIN} 
where USER_ID = #{USER_ID}

mapper.select:
	select * from SYS_APP_USER 
	where 1=1
	
  	and	USERNAME = #{USERNAME} and PASSWORD = #{PASSWORD}
	
	
	and USER_ID = #{USER_ID}

mapper.update:
update SYS_APP_USER 
	set 
		IP = #{IP}
	where 
		USERNAME = #{USERNAME}

update SYS_APP_USER set USERNAME = #{USERNAME}, NAME = #{NAME}, ROLE_ID = #{ROLE_ID}, BZ = #{BZ}, PHONE = #{PHONE}, SFID = #{SFID}, START_TIME = #{START_TIME}, END_TIME = #{END_TIME}, YEARS = #{YEARS}, STATUS = #{STATUS}
,PASSWORD = #{PASSWORD}
where USER_ID = #{USER_ID}

mapper.select:
select u.USER_ID, u.USERNAME, u.PASSWORD, u.LAST_LOGIN, u.NAME, u.IP, u.END_TIME, u.YEARS, u.STATUS, r.ROLE_ID, r.ROLE_NAME from SYS_APP_USER u, SYS_ROLE r where u.ROLE_ID = r.ROLE_ID and 1 = 1 and r.PARENT_ID = '7'
and u.USERNAME LIKE CONCAT(CONCAT('%', #{pd.USERNAME}),'%')
and u.ROLE_ID=#{pd.ROLE_ID}
and u.END_TIME >= #{pd.lastLoginStart}
and u.END_TIME <= #{pd.lastLoginEnd}
and u.STATUS=#{pd.STATUS}
