mapper.update:
update SYS_MENU set 
	MENU_NAME=#{MENU_NAME},
	MENU_URL =#{MENU_URL} ,
	PARENT_ID=#{PARENT_ID},
	MENU_ORDER=#{MENU_ORDER}
where MENU_ID=#{MENU_ID}

update SYS_MENU set 
	MENU_ICON=#{MENU_ICON}
where MENU_ID=#{MENU_ID}

mapper.delete:
delete from SYS_MENU 
where MENU_ID=#{MENU_ID} 
or PARENT_ID=#{MENU_ID}
